#!/bin/bash
##############################################################################
# Valid - DoD Issuing CA
##############################################################################

curl -v -X 'POST' \
  'http://localhost:5000/vss/v2/validate' \
  -H 'accept: application/json' \
  -H 'Content-Type: application/json' \
  -d '{
  "validationPolicyId": "c21f969b-5f03-333d-83e0-4f8f136e7682",
  "x509Certificate": ""
}' | jq

